	/**
	 * 时间格式转化
	 */
	private static Date parseDate(String checkoutTime) throws ParseException {
		SimpleDateFormat formatter = new SimpleDateFormat("HH:mm:ss");
		return formatter.parse(checkoutTime);
	}

	/**
	 * 时间格式转化
	 */
	private static Date parseDateToDay(String checkoutTime) throws ParseException {
		SimpleDateFormat formatter = new SimpleDateFormat("yyyy-MM-dd");
		return formatter.parse(checkoutTime);
	}

	/**
	 * 判断是否为周末
	 */
	private static Boolean isWeekEnd(Date date) {
		Calendar calendar = Calendar.getInstance();
		calendar.setTime(date);
		int dayOfWeek = calendar.get(Calendar.DAY_OF_WEEK);
		return dayOfWeek == Calendar.SATURDAY || dayOfWeek == Calendar.SUNDAY;
	}
